export declare const landingPage1HTML = "\n<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Nova Matrix</title>\n    <style>\n        * {\n            margin: 0;\n            padding: 0;\n            box-sizing: border-box;\n        }\n        \n        body {\n            font-family: 'Segoe UI', Arial, sans-serif;\n            background-color: #13141a;\n            color: #ffffff;\n            height: 100vh;\n            width: 100%;\n            overflow: hidden;\n            position: relative;\n        }\n        \n        /* Star background */\n        body:before {\n            content: '';\n            position: absolute;\n            width: 100%;\n            height: 100%;\n            background-image: radial-gradient(circle, rgba(255, 255, 255, 0.3) 1px, transparent 1px);\n            background-size: 50px 50px;\n            z-index: 0;\n            opacity: 0.3;\n        }\n        \n        .container {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            justify-content: center;\n            height: 100%;\n            position: relative;\n            z-index: 1;\n            padding: 20px;\n        }\n        \n        .header {\n            display: flex;\n            justify-content: space-between;\n            width: 100%;\n            position: absolute;\n            top: 30px;\n            padding: 0 30px;\n        }\n        \n        .logo {\n            display: flex;\n            align-items: center;\n            font-size: 14px;\n            font-weight: 500;\n            color: #ffffff;\n        }\n        \n        .menu-button {\n            border: none;\n            background: transparent;\n            color: #ffffff;\n            cursor: pointer;\n            font-size: 14px;\n            text-transform: uppercase;\n            letter-spacing: 2px;\n        }\n        \n        .orb-container {\n            position: relative;\n            width: 180px;\n            height: 180px;\n            margin-bottom: 40px;\n        }\n        \n        .orb {\n            width: 150px;\n            height: 150px;\n            background: radial-gradient(circle, #ffffff 35%, #e0e0e0 35%, #b0b0b0 30%);\n            border-radius: 50%;\n            margin: 15px;\n            box-shadow: 0 0 20px rgba(255, 255, 255, 0.5);\n        }\n        \n        .orb-ring {\n            position: absolute;\n            top: 0;\n            left: 0;\n            width: 180px;\n            height: 180px;\n            border: 2px solid #2a2a36;\n            border-radius: 50%;\n            box-shadow: inset 0 0 10px rgba(255, 255, 255, 0.2);\n        }\n        \n        .main-title {\n            font-size: 42px;\n            font-weight: bold;\n            letter-spacing: 10px;\n            text-align: center;\n            margin-bottom: 20px;\n            color: #ffffff;\n            text-transform: uppercase;\n        }\n        \n        .main-title span {\n            display: inline-block;\n            margin: 0 5px;\n        }\n        \n        .subtitle {\n            font-size: 14px;\n            letter-spacing: 2px;\n            text-align: center;\n            margin-bottom: 40px;\n            color: #b0b0b0;\n            text-transform: uppercase;\n        }\n        \n        .nav-button {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            text-decoration: none;\n            color: #ffffff;\n            margin-top: 20px;\n            cursor: pointer;\n        }\n        \n        .diamond {\n            width: 40px;\n            height: 40px;\n            background-color: transparent;\n            border: 2px solid rgba(255, 255, 255, 0.5);\n            transform: rotate(45deg);\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            margin-bottom: 10px;\n        }\n        \n        .diamond-inner {\n            width: 10px;\n            height: 10px;\n            background-color: rgba(255, 255, 255, 0.5);\n        }\n        \n        .continue-text {\n            font-size: 12px;\n            text-transform: uppercase;\n            letter-spacing: 2px;\n        }\n        \n        .footer {\n            position: absolute;\n            bottom: 20px;\n            width: 100%;\n            text-align: center;\n        }\n        \n        .progress {\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            gap: 15px;\n            margin-bottom: 20px;\n        }\n        \n        .progress-item {\n            width: 12px;\n            height: 12px;\n            border: 1px solid rgba(255, 255, 255, 0.5);\n            border-radius: 50%;\n        }\n        \n        .progress-item.active {\n            background-color: rgba(255, 255, 255, 0.5);\n        }\n        \n        .credit {\n            font-size: 10px;\n            color: #666;\n            text-transform: uppercase;\n            letter-spacing: 1px;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"header\">\n            <div class=\"logo\">\n                <div class=\"logo-icon\"></div>\n                <div>NOVA MATRIX</div>\n            </div>\n        </div>\n        \n        <div class=\"orb-container\">\n            <div class=\"orb\"></div>\n            <div class=\"orb-ring\"></div>\n        </div>\n        \n        <h1 class=\"main-title\">\n            <span>W</span>\n            <span>E</span>\n            <span>L</span>\n            <span>C</span>\n            <span>O</span>\n            <span>M</span>\n            <span>E</span>\n        </h1>\n        \n        <p class=\"subtitle\">Welcome to the Nova Matrix - A powerful matrix visualization tool</p>\n        \n        <a href=\"#\" class=\"nav-button\" data-action=\"next\">\n            <div class=\"diamond\">\n                <div class=\"diamond-inner\"></div>\n            </div>\n            <span class=\"continue-text\">Next</span>\n        </a>\n        \n        <div class=\"footer\">\n            <div class=\"progress\">\n                <div class=\"progress-item active\"></div>\n                <div class=\"progress-item\"></div>\n                <div class=\"progress-item\"></div>\n            </div>\n            <div class=\"credit\">Developed by Joshua Biondo</div>\n        </div>\n    </div>\n</body>\n</html>";
