export declare const landingPage2HTML = "<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Nova Matrix - Features</title>\n    <style>\n        * {\n            margin: 0;\n            padding: 0;\n            box-sizing: border-box;\n        }\n        \n        body {\n            font-family: 'Segoe UI', Arial, sans-serif;\n            background-color: #13141a;\n            color: #ffffff;\n            height: 100vh;\n            width: 100%;\n            overflow: hidden;\n            position: relative;\n        }\n        \n        /* Star background */\n        body:before {\n            content: '';\n            position: absolute;\n            width: 100%;\n            height: 100%;\n            background-image: radial-gradient(circle, rgba(255, 255, 255, 0.3) 1px, transparent 1px);\n            background-size: 50px 50px;\n            z-index: 0;\n            opacity: 0.3;\n        }\n        \n        .container {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            justify-content: center;\n            height: 100%;\n            position: relative;\n            z-index: 1;\n            padding: 20px;\n            overflow-y: auto;\n            min-height: 500px;\n            gap: clamp(15px, 2.5vh, 25px);\n            transition: opacity 0.5s ease;\n            opacity: 1;\n        }\n\n        .container.fade-out {\n            opacity: 0;\n        }\n        \n        .header {\n            display: flex;\n            justify-content: space-between;\n            align-items: center;\n            width: 100%;\n            position: absolute;\n            top: 0;\n            padding: 20px 30px;\n        }\n        \n        .logo {\n            display: flex;\n            align-items: center;\n            font-size: 14px;\n            font-weight: 500;\n            color: #ffffff;\n        }\n        \n        .main-title {\n            font-size: clamp(24px, 5vw, 36px);\n            font-weight: bold;\n            letter-spacing: clamp(4px, 1vw, 8px);\n            text-align: center;\n            margin-bottom: clamp(5px, 1.5vh, 10px);\n            color: #ffffff;\n            text-transform: uppercase;\n        }\n        \n        .subtitle {\n            font-size: 14px;\n            letter-spacing: 2px;\n            text-align: center;\n            margin-bottom: clamp(15px, 2vh, 30px);\n            color: #b0b0b0;\n            text-transform: uppercase;\n        }\n        \n        .feature-list {\n            text-align: center;\n            max-width: 600px;\n            padding: 0 10px;\n        }\n        \n        .feature-list ul {\n            list-style: none;\n            margin-top: 15px;\n        }\n        \n        .feature-list li {\n            margin-bottom: 10px;\n            font-size: 14px;\n            color: #d0d0d0;\n        }\n        \n        .nav-buttons {\n            display: flex;\n            gap: clamp(15px, 4vw, 30px);\n            margin-top: clamp(10px, 2vh, 20px);\n        }\n        \n        .nav-button {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            text-decoration: none;\n            color: #ffffff;\n            cursor: pointer;\n        }\n        \n        /* Button orbs */\n        .button-orb-container {\n            width: 50px;\n            height: 50px;\n            position: relative;\n            margin-bottom: 10px;\n        }\n        \n        .button-orb {\n            width: 30px;\n            height: 30px;\n            background: radial-gradient(circle, \n                rgba(255, 255, 255, 0.9) 15%, \n                rgba(255, 234, 130, 0.8) 30%, \n                rgba(255, 183, 101, 0.7) 45%, \n                rgba(255, 100, 80, 0.5) 60%,\n                transparent 75%);\n            border-radius: 50%;\n            position: absolute;\n            top: 50%;\n            left: 50%;\n            transform: translate(-50%, -50%);\n            box-shadow: \n                0 0 15px rgba(255, 234, 130, 0.4),\n                0 0 25px rgba(255, 183, 101, 0.2);\n            animation: buttonPulse 2s infinite alternate, rotate 15s infinite linear;\n            opacity: 0.9;\n        }\n        \n        .button-orb:before {\n            content: '';\n            position: absolute;\n            top: -15%;\n            left: -15%;\n            right: -15%;\n            bottom: -15%;\n            border-radius: 50%;\n            background: radial-gradient(circle, \n                transparent 60%, \n                rgba(255, 234, 130, 0.1) 70%, \n                rgba(255, 183, 101, 0.1) 80%, \n                transparent 100%);\n            z-index: -1;\n            animation: buttonGlow 3s infinite alternate;\n        }\n        \n        .button-orb:after {\n            content: '';\n            position: absolute;\n            width: 40%;\n            height: 40%;\n            top: 50%;\n            left: 50%;\n            transform: translate(-50%, -50%);\n            background: radial-gradient(circle, \n                rgba(255, 255, 255, 0.9) 0%, \n                rgba(255, 240, 180, 0.8) 50%, \n                transparent 100%);\n            border-radius: 50%;\n            box-shadow: 0 0 10px rgba(255, 240, 180, 0.5);\n            animation: buttonCoreGlow 1.5s infinite alternate;\n        }\n        \n        @keyframes buttonPulse {\n            0% {\n                transform: translate(-50%, -50%) scale(0.9);\n            }\n            100% {\n                transform: translate(-50%, -50%) scale(1.1);\n            }\n        }\n        \n        @keyframes buttonGlow {\n            0% {\n                opacity: 0.3;\n                box-shadow: 0 0 15px rgba(255, 234, 130, 0.2);\n            }\n            100% {\n                opacity: 0.6;\n                box-shadow: 0 0 25px rgba(255, 183, 101, 0.4);\n            }\n        }\n        \n        @keyframes buttonCoreGlow {\n            0% {\n                opacity: 0.7;\n                width: 36%;\n                height: 36%;\n            }\n            100% {\n                opacity: 1;\n                width: 43%;\n                height: 43%;\n            }\n        }\n        \n        @keyframes rotate {\n            0% {\n                background-position: 0% 0%;\n            }\n            100% {\n                background-position: 100% 100%;\n            }\n        }\n        \n        .continue-text {\n            font-size: 12px;\n            text-transform: uppercase;\n            letter-spacing: 2px;\n        }\n        \n        .footer {\n            width: 100%;\n            text-align: center;\n            margin-top: clamp(10px, 2vh, 20px);\n            position: relative;\n        }\n        \n        .progress {\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            gap: 15px;\n        }\n        \n        .progress-item {\n            width: 12px;\n            height: 12px;\n            border: 1px solid rgba(255, 255, 255, 0.5);\n            border-radius: 50%;\n        }\n        \n        .progress-item.active {\n            background-color: rgba(255, 255, 255, 0.5);\n        }\n        \n        .credit {\n            font-size: 10px;\n            color: #666;\n            text-transform: uppercase;\n            letter-spacing: 1px;\n        }\n        \n        /* Content wrapper to maintain consistent vertical centering */\n        .content-wrapper {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            max-width: 100%;\n            padding-top: 60px; /* Fixed space for header */\n        }\n        \n        /* Smoother responsive approach with no sudden jumps */\n        @media (max-width: 768px) {\n            .nav-buttons {\n                gap: calc(15px + (30 - 15) * ((100vw - 320px) / (768 - 320)));\n            }\n            \n            .progress {\n                gap: calc(10px + (15 - 10) * ((100vw - 320px) / (768 - 320)));\n            }\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"header\">\n            <div class=\"logo\">\n                <div>NOVA MATRIX</div>\n            </div>\n            <div class=\"progress\">\n                <div class=\"progress-item\"></div>\n                <div class=\"progress-item active\"></div>\n                <div class=\"progress-item\"></div>\n            </div>\n        </div>\n        \n        <div class=\"content-wrapper\">\n            <h1 class=\"main-title\">FEATURES</h1>\n            \n            <div class=\"feature-list\">\n                <p class=\"subtitle\">Nova Matrix offers powerful features for your data</p>\n                <ul>\n                    <li>\u2728 Highly customizable styling and formatting</li>\n                    <li>\u2728 Interactive expand/collapse functionality</li>\n                    <li>\u2728 Advanced subtotal calculations</li>\n                    <li>\u2728 Dynamic row and column sizing</li>\n                    <li>\u2728 Smart context menu for quick actions</li>\n                </ul>\n            </div>\n            \n            <div class=\"nav-buttons\">\n                <a href=\"#\" class=\"nav-button\" data-action=\"back\">\n                    <div class=\"button-orb-container\">\n                        <div class=\"button-orb\"></div>\n                    </div>\n                    <span class=\"continue-text\">Back</span>\n                </a>\n                <a href=\"#\" class=\"nav-button\" data-action=\"next\">\n                    <div class=\"button-orb-container\">\n                        <div class=\"button-orb\"></div>\n                    </div>\n                    <span class=\"continue-text\">Next</span>\n                </a>\n            </div>\n            \n            <div class=\"footer\">\n                <div class=\"credit\">Developed by Joshua Biondo</div>\n            </div>\n        </div>\n    </div>\n</body>\n</html>";
