export declare const landingPage3HTML = "<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Nova Matrix - Basic</title>\n    <style>\n        * {\n            margin: 0;\n            padding: 0;\n            box-sizing: border-box;\n        }\n        \n        body {\n            font-family: 'Segoe UI', Arial, sans-serif;\n            background-color: #13141a;\n            color: #ffffff;\n            height: 100vh;\n            width: 100%;\n            overflow: hidden;\n            position: relative;\n        }\n        \n        /* Star background */\n        body:before {\n            content: '';\n            position: absolute;\n            width: 100%;\n            height: 100%;\n            background-image: radial-gradient(circle, rgba(255, 255, 255, 0.3) 1px, transparent 1px);\n            background-size: 50px 50px;\n            z-index: 0;\n            opacity: 0.3;\n        }\n        \n        .container {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            justify-content: space-between;\n            height: 100%;\n            position: relative;\n            z-index: 1;\n            padding: 20px;\n            overflow-y: auto;\n            min-height: 600px;\n        }\n        \n        .header {\n            display: flex;\n            justify-content: space-between;\n            width: 100%;\n            position: absolute;\n            top: 30px;\n            padding: 0 30px;\n        }\n        \n        .logo {\n            display: flex;\n            align-items: center;\n            font-size: 14px;\n            font-weight: 500;\n            color: #ffffff;\n        }\n        \n        /* ENHANCED ORB CONTAINER */\n        .orb-container {\n            position: relative;\n            width: min(250px, 80vw);\n            height: min(250px, 80vw);\n            margin: 80px auto 40px auto;\n            perspective: 1200px;\n            transform-style: preserve-3d;\n            flex-shrink: 0;\n        }\n        \n        /* ENHANCED ORB */\n        .orb {\n            width: min(150px, 50vw);\n            height: min(150px, 50vw);\n            background: radial-gradient(circle, \n                rgba(255, 255, 255, 0.9) 15%, \n                rgba(255, 234, 130, 0.8) 30%, \n                rgba(255, 183, 101, 0.7) 45%, \n                rgba(255, 100, 80, 0.5) 60%,\n                transparent 75%);\n            border-radius: 50%;\n            position: absolute;\n            top: 50%;\n            left: 50%;\n            transform: translate(-50%, -50%);\n            box-shadow: \n                0 0 60px rgba(255, 234, 130, 0.4),\n                0 0 100px rgba(255, 183, 101, 0.2);\n            z-index: 3;\n            animation: pulse 4s infinite alternate, rotate 30s infinite linear;\n            opacity: 0.9;\n            min-width: 80px;\n            min-height: 80px;\n        }\n        \n        /* Orb glow ring */\n        .orb:before {\n            content: '';\n            position: absolute;\n            top: -15%;\n            left: -15%;\n            right: -15%;\n            bottom: -15%;\n            border-radius: 50%;\n            background: radial-gradient(circle, \n                transparent 60%, \n                rgba(255, 234, 130, 0.1) 70%, \n                rgba(255, 183, 101, 0.1) 80%, \n                transparent 100%);\n            z-index: -1;\n            animation: glow 6s infinite alternate;\n        }\n        \n        /* Orb inner core */\n        .orb:after {\n            content: '';\n            position: absolute;\n            width: calc(40% + 10px);\n            height: calc(40% + 10px);\n            top: 50%;\n            left: 50%;\n            transform: translate(-50%, -50%);\n            background: radial-gradient(circle, \n                rgba(255, 255, 255, 0.9) 0%, \n                rgba(255, 240, 180, 0.8) 50%, \n                transparent 100%);\n            border-radius: 50%;\n            box-shadow: 0 0 20px rgba(255, 240, 180, 0.5);\n            animation: coreGlow 3s infinite alternate;\n        }\n        \n        @keyframes pulse {\n            0% {\n                transform: translate(-50%, -50%) scale(0.9);\n            }\n            100% {\n                transform: translate(-50%, -50%) scale(1.1);\n            }\n        }\n        \n        @keyframes glow {\n            0% {\n                opacity: 0.4;\n                box-shadow: 0 0 30px rgba(255, 234, 130, 0.2);\n            }\n            100% {\n                opacity: 0.7;\n                box-shadow: 0 0 50px rgba(255, 183, 101, 0.4);\n            }\n        }\n        \n        @keyframes coreGlow {\n            0% {\n                opacity: 0.7;\n                width: calc(36% + 10px);\n                height: calc(36% + 10px);\n            }\n            100% {\n                opacity: 1;\n                width: calc(43% + 10px);\n                height: calc(43% + 10px);\n            }\n        }\n        \n        @keyframes rotate {\n            0% {\n                background-position: 0% 0%;\n            }\n            100% {\n                background-position: 100% 100%;\n            }\n        }\n        \n        .main-title {\n            font-size: clamp(24px, 5vw, 36px);\n            font-weight: bold;\n            letter-spacing: 8px;\n            text-align: center;\n            margin-bottom: 20px;\n            color: #ffffff;\n            text-transform: uppercase;\n        }\n        \n        .subtitle {\n            font-size: 14px;\n            letter-spacing: 2px;\n            text-align: center;\n            margin-bottom: 30px;\n            color: #b0b0b0;\n            text-transform: uppercase;\n        }\n        \n        .feature-list {\n            text-align: center;\n            margin-bottom: 30px;\n            max-width: 600px;\n            padding: 0 10px;\n        }\n        \n        .feature-list ul {\n            list-style: none;\n            margin-top: 15px;\n        }\n        \n        .feature-list li {\n            margin-bottom: 10px;\n            font-size: 14px;\n            color: #d0d0d0;\n        }\n        \n        .nav-buttons {\n            display: flex;\n            gap: 30px;\n            margin-top: 20px;\n        }\n        \n        .nav-button {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            text-decoration: none;\n            color: #ffffff;\n            cursor: pointer;\n        }\n        \n        .diamond {\n            width: 40px;\n            height: 40px;\n            background-color: transparent;\n            border: 2px solid rgba(255, 255, 255, 0.5);\n            transform: rotate(45deg);\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            margin-bottom: 10px;\n        }\n        \n        .diamond-inner {\n            width: 10px;\n            height: 10px;\n            background-color: rgba(255, 255, 255, 0.5);\n        }\n        \n        .continue-text {\n            font-size: 12px;\n            text-transform: uppercase;\n            letter-spacing: 2px;\n        }\n        \n        .footer {\n            width: 100%;\n            text-align: center;\n            margin-top: 40px;\n            position: relative;\n        }\n        \n        .progress {\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            gap: 15px;\n            margin-bottom: 20px;\n        }\n        \n        .progress-item {\n            width: 12px;\n            height: 12px;\n            border: 1px solid rgba(255, 255, 255, 0.5);\n            border-radius: 50%;\n        }\n        \n        .progress-item.active {\n            background-color: rgba(255, 255, 255, 0.5);\n        }\n        \n        .credit {\n            font-size: 10px;\n            color: #666;\n            text-transform: uppercase;\n            letter-spacing: 1px;\n        }\n            /* Media queries for responsiveness */\n        @media (max-height: 700px) {\n            .container {\n                justify-content: flex-start;\n                padding-top: 70px;\n            }\n            \n            .orb-container {\n                margin: 20px auto;\n            }\n            \n            .feature-list {\n                margin-bottom: 20px;\n            }\n            \n            .nav-buttons {\n                margin-top: 10px;\n            }\n            \n            .footer {\n                margin-top: 20px;\n            }\n        }\n        \n        @media (max-width: 500px) {\n            .nav-buttons {\n                gap: 15px;\n            }\n            \n            .diamond {\n                width: 30px;\n                height: 30px;\n            }\n            \n            .progress {\n                gap: 10px;\n            }\n            \n            .progress-item {\n                width: 10px;\n                height: 10px;\n            }\n        }\n        \n        /* Hide orb on very small screens */\n        @media (max-width: 400px), (max-height: 500px) {\n            .orb-container {\n                display: none;\n            }\n            \n            .container {\n                padding-top: 60px;\n            }\n            \n            .main-title {\n                margin-top: 20px;\n            }\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"header\">\n            <div class=\"logo\">\n                <div>NOVA MATRIX</div>\n            </div>\n        </div>\n        \n        <div class=\"orb-container\">\n            <div class=\"orb\"></div>\n        </div>\n        \n        <h1 class=\"main-title\">INSTRUCTIONS</h1>\n        \n        <div class=\"feature-list\">\n            <p class=\"subtitle\">Begin presenting your data to viewers by:</p>\n            <ul>\n                <li>\u2728 Adding a Matrix visual to your report</li>\n                <li>\u2728 Configure the Rows, Columns, and Values fields</li>\n                <li>\u2728 Use the formatting panel to customize your matrix</li>\n                <li>\u2728 Enjoy the power of interactive data exploration!</li>\n            </ul>\n        </div>\n        \n        <div class=\"nav-buttons\">\n            <a href=\"#\" class=\"nav-button\">\n                <div class=\"diamond\">\n                    <div class=\"diamond-inner\"></div>\n                </div>\n                <span class=\"continue-text\">Back</span>\n            </a>\n            <a href=\"#\" class=\"nav-button\">\n                <div class=\"diamond\">\n                    <div class=\"diamond-inner\"></div>\n                </div>\n                <span class=\"continue-text\">Next</span>\n            </a>\n        </div>\n        \n        <div class=\"footer\">\n            <div class=\"progress\">\n                <div class=\"progress-item\"></div>\n                <div class=\"progress-item\"></div>\n                <div class=\"progress-item active\"></div>\n            </div>\n            <div class=\"credit\">Developed by Joshua Biondo</div>\n        </div>\n    </div>\n</body>\n</html>";
