content.css:
.visual-container {
  width: 100%;
  height: 100%;
  overflow: hidden;
  /* Change from auto to hidden */
}
.table-container {
  width: 100%;
  height: 100%;
  overflow: auto;
  position: relative;
  /* Add this */
}
.matrix-table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  font-family: "Segoe UI", sans-serif;
  font-size: 12px;
  text-rendering: geometricPrecision;
  -webkit-font-smoothing: antialiased;
}
.matrix-table thead {
  position: sticky;
  top: 0;
  z-index: 10;
}
.matrix-table th {
  padding: 10px;
  background-color: #f4f4f4;
  text-align: center;
  font-weight: 600;
  position: sticky;
  top: 0;
  z-index: 5;
}
.matrix-table th.row-header {
  left: 0;
  position: sticky;
  z-index: 15;
}
.matrix-table td {
  padding: 10px;
  text-align: right;
}
.matrix-corner-cell {
  position: sticky;
  top: 0;
  left: 0;
  z-index: 10;
  background: inherit;
}
.matrix-table .column-header {
  background-color: #e0e0e0;
}
.matrix-table .row-header {
  background-color: #e8e8e8;
}
/* Update these selectors for better sticky behavior */
.matrix-table th.column-header {
  position: sticky;
  top: 0;
  z-index: 5;
}
.matrix-table th.row-header {
  position: sticky;
  left: 0;
  z-index: 5;
}
/* This won't be used due to our inline styles, but good for completeness */
.matrix-table th.row-header.column-header {
  position: sticky;
  top: 0;
  left: 0;
  z-index: 10;
}
/* Rest of the existing styles remain the same */
.matrix-table .subtotal-cell {
  font-weight: bold;
}
.toggle-button {
  width: 16px;
  height: 16px;
  margin-right: 5px;
  display: inline-block;
  text-align: center;
  font-size: 12px;
  line-height: 16px;
}
.toggle-spacer {
  display: inline-block;
  width: 16px;
  margin-right: 5px;
}
.matrix-table.hover-enabled tbody tr:hover {
  background-color: rgba(0, 120, 212, 0.1) !important;
}
/* Border control styles - existing styles */
.matrix-table.with-borders {
  border-collapse: collapse;
}
.matrix-table.with-borders th,
.matrix-table.with-borders td {
  border-style: var(--border-style, solid);
  border-width: 0;
  border-color: var(--border-color, #ffffff);
}
.matrix-table.with-horizontal-borders th,
.matrix-table.with-horizontal-borders td {
  border-top-width: var(--border-width, 1px);
  border-bottom-width: var(--border-width, 1px);
}
.matrix-table.with-vertical-borders th,
.matrix-table.with-vertical-borders td {
  border-left-width: var(--border-width, 1px);
  border-right-width: var(--border-width, 1px);
}
matrix-table tfoot {
  z-index: 5;
}
.matrix-table tfoot th,
.matrix-table tfoot td {
  padding: 10px;
}
.matrix-table tr {
  transition: all 0.3s ease-out;
}
.matrix-table .expandable-row {
  overflow: hidden;
}
.matrix-table .collapsed {
  display: none !important;
  height: 0 !important;
  opacity: 0 !important;
  pointer-events: none !important;
}
.matrix-table .expanding {
  animation: expand 0.3s ease-out forwards;
}
.matrix-table .collapsing {
  animation: collapse 0.3s ease-out forwards;
}
@keyframes expand {
  from {
    height: 0;
    opacity: 0;
  }
  to {
    height: var(--row-height);
    opacity: 1;
  }
}
@keyframes collapse {
  from {
    height: var(--row-height);
    opacity: 1;
  }
  to {
    height: 0;
    opacity: 0;
    display: none;
  }
}
@keyframes expandWave {
  0% {
    height: 0;
    opacity: 0;
    transform: scaleY(0.3) translateY(-10px);
  }
  30% {
    height: calc(var(--row-height) * 1.05);
    opacity: 0.7;
    transform: scaleY(1.05) translateY(0);
  }
  100% {
    height: var(--row-height);
    opacity: 1;
    transform: scaleY(1) translateY(0);
  }
}
@keyframes collapseWave {
  0% {
    height: var(--row-height);
    opacity: 1;
    transform: scaleY(1) translateY(0);
  }
  30% {
    height: calc(var(--row-height) * 0.95);
    opacity: 0.7;
    transform: scaleY(0.95) translateY(0);
  }
  100% {
    height: 0;
    opacity: 0;
    transform: scaleY(0.3) translateY(-10px);
  }
}
.matrix-table .expanding-wave {
  animation: expandWave 0.2s ease-out forwards;
  transform-origin: top;
  overflow: hidden;
  will-change: height, opacity, transform;
  pointer-events: none;
  /* Prevent clicks during animation */
}
.matrix-table .collapsing-wave {
  animation: collapseWave 0.2s ease-in forwards;
  transform-origin: top;
  overflow: hidden;
  will-change: height, opacity, transform;
  pointer-events: none;
  /* Prevent clicks during animation */
}
.custom-context-menu {
  background-color: white;
  border: 1px solid #ccc;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  border-radius: 4px;
  padding: 5px 0;
  min-width: 150px;
}
.context-menu-item {
  padding: 8px 12px;
  cursor: pointer;
  user-select: none;
}
.context-menu-item:hover {
  background-color: #f2f2f2;
}
.context-menu-separator {
  height: 1px;
  background-color: #e0e0e0;
  margin: 5px 0;
}
